gsap.registerPlugin(ScrollTrigger);

const allowStartAnimation = true;

if (allowStartAnimation) {
  let e = document.querySelectorAll(".hero-title");
  e.forEach((e) => {
    e.style.transform = "translateY(100%)";
  }),
    gsap.fromTo(
      ".hero-subtitle",
      { y: "100%" },
      { y: 0, duration: 1.2, delay: 1, ease: "power2.inOut" }
    ),
    setTimeout(() => {
      e.forEach((e, t) => {
        gsap.fromTo(
          e,
          { y: "100%" },
          { y: 0, duration: 1.4, delay: 0.04 * t, ease: "power4.inOut" }
        );
      });
    }, 2400);
}
